import \'package:flutter/material.dart\';\nimport \'package:myapp/models/vehicle.dart\';\n\nclass VehicleDetailScreen extends StatelessWidget {\n  final Vehicle vehicle;\n\n  const VehicleDetailScreen({super.key, required this.vehicle});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: Text(vehicle.name),\n      ),\n      body: Padding(\n        padding: const EdgeInsets.all(16.0),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            Text(\'Type: \${vehicle.type}\', style: const TextStyle(fontSize: 18)),\n            const SizedBox(height: 10),\n            Text(\'Status: \${vehicle.status}\', style: const TextStyle(fontSize: 18)),\n            const SizedBox(height: 10),\n            Text(\'Location: \${vehicle.location}\', style: const TextStyle(fontSize: 18)),\n            const SizedBox(height: 10),\n            if (vehicle.details != null && vehicle.details!.isNotEmpty)\n              Text(\'Details: \${vehicle.details}\', style: const TextStyle(fontSize: 18)),\n            const SizedBox(height: 10),\n            Text(\n              \'Last Updated: \${vehicle.lastUpdated.toLocal()}\',\n              style: const TextStyle(fontSize: 16, color: Colors.grey),\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}\n